import java.awt.*;import java.awt.event.*;import javax.swing.*;// A panel that draws animated rectangles on its surface.public class Animated extends JPanel implements ActionListener {	private static final long serialVersionUID = 1L;		static JButton btn1 = new JButton("1");	static JButton btn2 = new JButton("2");	static JButton btn3 = new JButton("3");	int index = 1;		public static void main(String[] args) {		JFrame frame = new JFrame();		frame.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);		frame.setSize(400, 200);		frame.setTitle("A panel with rectangles");		frame.setLayout(new BorderLayout());				JPanel north = new JPanel(new FlowLayout());		north.add(btn1);			north.add(btn2);			north.add(btn3);		frame.add(north, BorderLayout.NORTH);					Animated panel = new Animated();				panel.setBackground(Color.WHITE);		frame.add(panel, BorderLayout.CENTER);				frame.setVisible(true);	}	    public Animated() {        // set up the timer to animate the rectangles        Timer time = new Timer(1000, this);  // update every 1000 ms        time.start();    }    // draws two rectangles on this panel on the screen    public void paintComponent(Graphics g) {        super.paintComponent(g);  // call JPanel's original version    }    // called at regular intervals by timer to redraw the panel    public void actionPerformed(ActionEvent event) {        	if(index == 1) {		btn1.setText("Hello");		index++;	}	else if(index == 2) {		btn2.setText("Good");		index++;	}else if(index == 3) {		btn3.setText("Students");		index--;	}		repaint();    }    }